mapper.update:
update teller
set teller_name = #{tellerName,jdbcType=VARCHAR},
  locked = #{locked,jdbcType=VARCHAR},
  branch_id = #{branchId,jdbcType=VARCHAR}
where teller_id = #{tellerId,jdbcType=VARCHAR}

mapper.select:
select teller_id, teller_name, locked, branch_id
from teller
where teller_id = #{tellerId,jdbcType=VARCHAR}
